import React, { useEffect,useState } from 'react'
import AOS from 'aos';
import 'aos/dist/aos.css';
import './TopProject.css'
import OwlCarousel from 'react-owl-carousel';
import 'owl.carousel/dist/assets/owl.carousel.css';
import 'owl.carousel/dist/assets/owl.theme.default.css'
import data from '../exa'
const options = {
    margin: 30,
    responsiveClass: true,
    nav: true,
    autoplay: false,
    navText: ["Prev", "Next"],
    smartSpeed: 1000,
    responsive: {
        0: {
            items: 1,
        },
        400: {
            items: 1,
        },
        600: {
            items: 2,
        },
        700: {
            items: 3,
        },
        1000: {
            items: 5,
        }
    },
  };
const TopProject=()=>{
    useEffect(()=>{
        AOS.init();
      },[]);  
    return (
      <div>
        <section class="site-top-projects">
            <h2>The most popular projects </h2>
        </section>
        <section  className = "py-4 py-lg-5 container">
            <div class="blog">
                <OwlCarousel className="slider-items owl-carousel" {...options}>
                {data.posts.map((item,index)=>
              {
                return (
                        <div class="blog-content" data-aos="fade-up" data-aos-offset="100" >
                                <img src={item.imgSrc} height="170rem" alt="project1" />
                                <div class="blog-title">
                                    <h3>Project NÂ°1</h3>
                                    <p>
                                        {item.disc}
                                    </p>
                                    <div class="progress">
                                        <div class="progress-done" id="PRG1" data-done={item.pro}>
                                            {item.pro}
                                        </div>
                                        <span>28<b>/</b>30 interested</span>
                                    </div>
                                    <button class="btn btn-blog">See more details</button>
                                    {/* <h6>View more details</h6>  */}
                                </div>
                            </div>
                            )
                            }
                            )
                            }
                </OwlCarousel>
            </div>
            <div class="container">
                <div class="owl-carousel owl-theme blog-post">   
                    <div class="owl-navigation">
                        <span class="owl-nav-prev"><i class="fas fa-long-arrow-alt-left"></i></span>
                        <span class="owl-nav-next"><i class="fas fa-long-arrow-alt-right"></i></span>
                    </div>
                </div>
            </div>
        </section>
      </div>
    )
  }
export default  TopProject;
